import Card from '../UI/Card';
import classes from './ProductItem.module.css';
import {useDispatch} from "react-redux"
import { cartActions } from '../../store/cart-slice';
import {useSelector} from 'react-redux'

const ProductItem = (props) => {
  const { id,title, price, description } = props;
  const cart=useSelector(state=>state.cart)
  const dispatch=useDispatch()

  const addCartHendler=()=>{
    const newTotalQuantity=cart.totalQuantity+1
    const updateItem=cart.items.slice()
    const existingItem=updateItem.find((item)=>item.id==id)
    if(existingItem){
      const updatedItem={...existingItem}
      updateItem.quantity++;
      updateItem.price=updatdItem.price+price;
      const existingItemIndex=updatdItem.findIndex(item=>item.id==id)
      updateItem[existingItemIndex]= updatdItem;
    }else{
      updatedItem.push({
        id:id, price:price,quantity:1,totalPrice:price,name:title
      })
    }
    const newCart={
      totalQuantity:newTotoalQuantity,
      items:updatedItem,
    }
    dispatch(cartActions.addItemsToCart({
      id,
      title,
      price
    }))
    dispatch(cartActions.replaceCart(newCart))
  };


  return (
    <li className={classes.item}>
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={classes.price}>{price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={classes.actions}>
          <button onClick={addCartHendler}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
