import Card from '../UI/Card';
import classes from './ProductItem.module.css';
import {useDispatch} from "react-redux"
import { cartActions } from '../../store/cart-slice';
import {useSelector} from '../../store/cart-slice'

const ProductItem = (props) => {
  const { id,title, price, description } = props;
  const cart=useSelector(state=>state.cart)
  const dispatch=useDispatch()

  const addCartHendler=()=>{
    dispatch(cartActions.addItemsToCart({
      id,
      title,
      price
    }))
  };

  const addItemToCartHendler=()=>{
    const newTotalQuantity=cart.totalQuantity+1
    const updateItem=cart.items.slice()
    const existingItem=updateItem.find((item)=>item.id==id)
    if(existingItem){
      const updatedItem={...existingItem}
      updatedItem.quantity++;
      updatedItem.price=updatedItem.price+price;
    }
  }

  return (
    <li className={classes.item}>
      <Card>
        <header>
          <h3>{title}</h3>
          <div className={classes.price}>{price.toFixed(2)}</div>
        </header>
        <p>{description}</p>
        <div className={classes.actions}>
          <button onClick={addCartHendler}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
